value.value:
{
  // Create nested lists structure
  nestedData = list(
    list("A", "B"), 
    list("C", "D", "E"), 
    list("F")
  );

  // Use flatMap to flatten by returning each sublist directly
  result = $nestedData.flatMap(sublist -> $sublist);

  $result;
}

{
  // Sample data with different word lengths
  words = list("cat", "elephant", "dog", "hippopotamus", "bird");

  // For words longer than 4 characters, return both original and first 4 chars
  // For shorter words, return just the original
  result = $words.flatMap(word -> 
    if($word.length() > 4,
       list($word, $word.subString(0, 4)),
       list($word)
    )
  );

  $result;
}

{
  // Mixed data with empty lists and content
  mixedData = list(
    list("first", "second"),
    list(),  // Empty list
    list("third"),
    list("fourth", "fifth", "sixth")
  );

  // FlatMap should handle empty lists gracefully
  result = $mixedData.flatMap(sublist -> $sublist);

  $result;
}

{
  // Numbers for transformation
  numbers = list(1, 2, 3, 4, 5);

  // Return single values for odd numbers, pairs for even numbers
  result = $numbers.flatMap(num ->
    if($num % 2 == 1,
       $num,  // Single value for odd numbers
       list($num, $num * 10)  // Collection for even numbers
    )
  );

  $result;
}

{
  // Simulate department-employee structure
  departments = list(
    {
      "name": "Engineering",
      "employees": list("Alice", "Bob", "Charlie")
    },
    {
      "name": "Sales", 
      "employees": list("Diana", "Eve")
    },
    {
      "name": "Marketing",
      "employees": list("Frank")
    }
  );

  // Extract all employee names with their department info
  result = $departments.flatMap(dept ->
    $dept["employees"].map(emp -> 
      toString($dept["name"], ": ", $emp)
    )
  );

  $result;
}

{
  // Sample hierarchical data
  categories = list(
    {"name": "Electronics", "items": list("Phone", "Laptop", "Tablet")},
    {"name": "Books", "items": list("Novel", "Textbook")},
    {"name": "Clothing", "items": list("Shirt", "Pants", "Shoes", "Hat")}
  );

  // Method 1: Using flatMap (efficient)
  flatMapResult = $categories.flatMap(category -> $category["items"]);

  // Method 2: Using map().flatten() (less efficient)
  mapFlattenResult = $categories
    .map(category -> $category["items"])
    .flatten();

  // Both should produce identical results
  comparison = {
    "flatMapResult": $flatMapResult,
    "mapFlattenResult": $mapFlattenResult,
    "resultsEqual": $flatMapResult.size() == $mapFlattenResult.size()
  };

  $comparison;
}
