value.value:
{
  // Test data: two lists with equal length
  testLists = list(
    list("A", "B", "C"),
    list(1, 2, 3)
  );

  // Execute transpose
  result = $testLists.transpose();

  $result.flatten();
}

{
  // Test data: two lists of strings
  testLists = list(
    list("Hello", "Good", "See"),
    list("World", "Day", "You")
  );

  // Execute transpose with combination function
  result = $testLists.transpose(first -> second -> 
    toString($first, " ", $second, "!")
  );

  $result;
}

{
  // Test data: lists of different lengths
  testLists = list(
    list(10, 20, 30, 40, 50),
    list(1, 2, 3)
  );

  // Execute transpose with null-safe addition
  result = $testLists.transpose(a -> b -> 
    if($b == null, $a, $a + $b)
  );

  $result;
}

{
  // Test data: collection with only one list
  testLists = list(
    list("Alpha", "Beta", "Gamma")
  );

  // Execute transpose without function
  result = $testLists.transpose();

  $result
}

{
  // Test data: one empty list and one with elements
  testLists = list(
    list(),
    list("X", "Y", "Z")
  );

  // Execute transpose with string concatenation function
  result = $testLists.transpose(first -> second -> 
    toString(
      if($first == null, "EMPTY", $first), 
      "-", 
      if($second == null, "NULL", $second)
    )
  );

  $result;
}

{
  // Test data: lists with mixed data types
  testLists = list(
    list("product1", "product2", "product3"),
    list(100, 250, 175)
  );

  // Execute transpose creating structured objects
  result = $testLists.transpose(name -> price -> {
    "productName": $name,
    "price": $price,
    "category": if($price > 200, "Premium", "Standard"),
    "displayText": toString($name, " ($", $price, ")")
  });

  $result;
}

{
  // Test data: student names and their test scores
  testLists = list(
    list("Alice", "Bob", "Charlie", "Diana", "Eve", "Frank"),
    list(95, 67, null, 88, 45, 92)
  );

  // Execute transpose with comprehensive student analysis
  result = $testLists.transpose(studentName -> score -> {
    // Validate inputs
    validName = if($studentName == null, 
                   throw("Student name cannot be null"), 
                   $studentName);

    // Handle missing scores
    validScore = if($score == null, 0, $score);

    // Calculate letter grade
    letterGrade = if($validScore >= 90, "A",
                    if($validScore >= 80, "B",
                      if($validScore >= 70, "C",
                        if($validScore >= 60, "D", "F"))));

    // Determine performance category
    performanceLevel = if($validScore >= 85, "Excellent",
                         if($validScore >= 75, "Good",
                           if($validScore >= 65, "Average",
                             if($validScore >= 50, "Below Average", "Poor"))));

    // Calculate points for GPA (4.0 scale)
    gpaPoints = if($letterGrade == "A", 4.0,
                  if($letterGrade == "B", 3.0,
                    if($letterGrade == "C", 2.0,
                      if($letterGrade == "D", 1.0, 0.0))));

    // Create comprehensive student record
    {
      "studentName": $validName,
      "rawScore": $score,
      "adjustedScore": $validScore,
      "letterGrade": $letterGrade,
      "gpaPoints": $gpaPoints,
      "performanceLevel": $performanceLevel,
      "needsHelp": $validScore < 70,
      "isHonorRoll": $validScore >= 90,
      "status": if($score == null, "INCOMPLETE", 
                  if($validScore < 60, "FAILING", "PASSING")),
      "feedback": toString($validName, " scored ", 
                          if($score == null, "INCOMPLETE", toString($validScore)), 
                          " (", $letterGrade, ") - ", $performanceLevel),
      "metadata": {
        "processed": true,
        "hasOriginalScore": $score != null,
        "scoreCategory": if($validScore >= 80, "high", 
                           if($validScore >= 60, "medium", "low"))
      }
    };
  });

  $result;
}
