value.value:
{
    x = 10;
    result = try(
        $x * 2,
        error -> "Error occurred: " + $error
    );
    $result; // Expected: 20
}

{
    x = "test";
    result = try(
        throw("This is a test error"),
        error -> "Caught: " + $error
    );
    $result; // Expected: "Caught: This is a test error"
}

{
    x = "Hello World";
    result = try(
        toUpperCase($x),
        error -> "Failed to process: " + $error
    );
    $result; // Expected: "HELLO WORLD"
}

{
    x = -5;
    result = try(
        {
            if($x < 0, throw("Negative values not allowed: " + $x), $x * $x);
        },
        error -> {
            logMessage = toString("Validation failed: ", $error);
            $logMessage;
        }
    );
    $result;
}

{
    numbers = list(1, 2, 3, 4, 5);
    result = try(
        {
            doubled = $numbers.map(n -> $n * 2);
            filtered = $doubled.filter(n -> $n > 4);
            {
                "original": $numbers,
                "processed": $filtered,
                "count": $filtered.size()
            };
        },
        error -> {
            "error": true,
            "message": toString("Processing failed: ", $error),
            "fallback": list()
        }
    );
    $result;
}

{
    x = generateSequenceId("123");

    result = try(
        messageFormat("Good {0}!").parse($x),
        error -> -1  // Return default number instead of string
    );

    $result; // Expected: -1

}

{
    email = "valid@email.com";
    result = try(
        $email.stringContains("@"),
        error -> false
    );
    $result; // Expected: true
}

{
    data = "test-data";
    result = try(
        {
            // Nested try-catch
            nestedResult = try(
                {
                    if($data == "test-data", 
                       "Inner success", 
                       throw("Inner validation failed"));
                },
                innerError -> "Inner catch: " + $innerError
            );
            "Outer success: " + $nestedResult;
        },
        error -> "Outer catch: " + $error
    );
    $result; // Expected: "Outer success: Inner success"
}

{
    data = "fail-me";
    recoveryData = "recovery-data";
    result = try(
        throw("First error"),
        error -> {
            // Try to recover with nested try-catch
            recoveryResult = try(
                "Recovered with: " + $recoveryData,
                recoveryError -> "Recovery also failed"
            );
            "Caught: "+ $error + ". " + $recoveryResult;
        }
    );
    $result;
}

{
    x = 5;
    result = try(
        {
            doubled = $x * 2;
            level1 = try(
                {
                    sum = $doubled + 10;
                    level2 = try(
                        $sum / 2,
                        error -> "Level 2 error"
                    );
                    $level2;
                },
                error -> "Level 1 error"
            );
            $level1;
        },
        error -> "Top level error"
    );
    $result; // Expected: 10 ((5*2)+10)/2 = 20/2 = 10
}

{
    stringNumbers = list("1", "2", "invalid", "4", "5");
    result = try(
        $stringNumbers
            .map(str -> try(
                numberFormat("#").parse($str),
                error -> 0
            ))
            .filter(num -> $num > 0),
        error -> list()
    );
    $result;
}

{
    num = 15;
    result = try(
        {
            processedValue = if($num > 10, $num * 2, $num);
            category = $processedValue > 20 ? "high" : "normal";
            {
                "value": $processedValue,
                "category": $category,
                "success": true
            };
        },
        error -> {
            "value": 0,
            "category": "error",
            "success": false,
            "error": toString($error)
        }
    );
    $result;
}

{
    str = "hello";
    result = try(
            toUpperCase($str),
            error -> "DEFAULT"
        )
        .with(tryResult -> 
            try(
                $tryResult + " WORLD",
                error -> "FALLBACK"
            )
        );
    $result; // Expected: "HELLO WORLD"
}

{
    people = list(
        {"name": "Alice", "age": "25"},
        {"name": "Bob", "age": "invalid"},
        {"name": "Charlie", "age": "30"}
    );

    result = try(
        $people.map(person -> {
            validAge = try(
                numberFormat("#").parse($person["age"]),
                error -> 0
            );
            {
                "name": $person["name"],
                "age": $validAge,
                "valid": $validAge > 0
            };
        }),
        error -> list()
    );
    $result;
}

{
    config = {
        "users": list("user1", "user2", "user3"),
        "operation": "validate"
    };

    result = try(
        {
            userList = $config["users"];
            operation = $config["operation"];

            processedUsers = try(
                $userList
                    .filter(user -> $user != null)
                    .map(user -> {
                        processed = try(
                            toUpperCase($user),
                            error -> "INVALID_USER"
                        );
                        $processed;
                    }),
                error -> list("FALLBACK_USER")
            );

            {
                "operation": $operation,
                "processedUsers": $processedUsers,
                "count": $processedUsers.size(),
                "status": "success"
            };
        },
        error -> {
            "operation": "unknown",
            "processedUsers": list(),
            "count": 0,
            "status": "failed",
            "error": toString($error)
        }
    );
    $result;
}

{
    emptyStr = "";
    result = try(
        {
            if($emptyStr.length() == 0, 
               throw("Empty string not allowed"), 
               $emptyStr);
        },
        error -> "Default value for empty input"
    );
    $result;
}

{
    emptyStr = "";
    result = try(
        {
            if($emptyStr.length() == 0, 
               throw("Empty string not allowed"), 
               $emptyStr);
        }
    );
    $result == null;
}
