value.value:
{
    argument = 10;
    result = $argument.try(
        x -> $x * 2,
        error -> "Error occurred: " + $error
    );
    $result; // Expected: 20
}

{
    argument = "test";
    result = $argument.try(
        x -> throw("This is a test error"),
        error -> "Caught: " + $error
    );
    $result; // Expected: "This is a test error"
}

{
    argument = "Hello World";
    result = $argument.try(
        x -> toUpperCase($x),
        error -> "Failed to process: " + $error
    );
    $result; // Expected: "HELLO WORLD"
}

{
    argument = 0;
    result = $argument.try(
        x -> {
            y = $x - 5;
            if($y < 0, throw(message: "Negative values not allowed.", value: $y), $x * $y);
        },
        error -> origArgument -> givenValue -> {
            logMessage = toString("Validation with argument '" + $origArgument + "' and resulting value '" + $givenValue + "' failed. Reason: ", $error);
            $logMessage;
        }
    );
    $result;
}

{
    argument = list(1, 2, 3, 4, 5);
    result = $argument.try(
        numbers -> {
            doubled = $numbers.map(n -> $n * 2);
            filtered = $doubled.filter(n -> $n > 4);
            {
                "original": $numbers,
                "processed": $filtered,
                "count": $filtered.size()
            };
        },
        error -> {
            "error": true,
            "message": toString("Processing failed: ", $error),
            "fallback": list()
        }
    );
    $result;
}

{

    argument = "not-a-number";

    result = $argument.try(

        x -> messageFormat("Good {0}!").parse($x),

        error -> -1  // Return default number instead of string

    );

    $result; // Expected: -1

}

{
    argument = "valid@email.com";
    result = $argument.try(
        email -> $email.stringContains("@"),
        error -> false
    );
    $result; // Expected: true
}

{
    argument = "test-data";
    result = $argument.try(
        data -> {
            // Nested try-catch
            nestedResult = $data.try(
                innerData -> {
                    if($innerData == "test-data", 
                       "Inner success", 
                       throw("Inner validation failed"));
                },
                innerError -> "Inner catch: " + $innerError
            );
            "Outer success: " + $nestedResult;
        },
        error -> "Outer catch: " + $error
    );
    $result; // Expected: "Outer success: Inner success"
}

{
    argument = "fail-me";
    result = $argument.try(
        data -> throw("First error"),
        error -> {
            // Try to recover with nested try-catch
            recoveryResult = "recovery-data".try(
                recoveryData -> "Recovered with: " + $recoveryData,
                recoveryError -> "Recovery also failed"
            );
            "Caught first error, recovery: " + $recoveryResult;
        }
    );
    $result;
}

{
    argument = 5;
    result = $argument.try(
        x -> {
            level1 = ($x * 2).try(
                doubled -> {
                    level2 = ($doubled + 10).try(
                        sum -> $sum / 2,
                        error -> "Level 2 error"
                    );
                    $level2;
                },
                error -> "Level 1 error"
            );
            $level1;
        },
        error -> "Top level error"
    );
    $result; // Expected: 10 ((5*2)+10)/2 = 20/2 = 10
}

{
    argument = list("1", "2", "invalid", "4", "5");
    result = $argument.try(
        stringNumbers -> $stringNumbers
            .map(str -> $str.try(
                s -> numberFormat("#").parse($s),
                error -> 0
            ))
            .filter(num -> $num > 0),
        error -> list()
    );
    $result;
}

{
    argument = 15;
    result = $argument.try(
        num -> {
            processedValue = if($num > 10, $num * 2, $num);
            category = $processedValue > 20 ? "high" : "normal";
            {
                "value": $processedValue,
                "category": $category,
                "success": true
            };
        },
        error -> {
            "value": 0,
            "category": "error",
            "success": false,
            "error": toString($error)
        }
    );
    $result;
}

{
    argument = "hello";
    result = $argument
        .try(
            str -> toUpperCase($str),
            error -> "DEFAULT"
        )
        .try(
            upperStr -> $upperStr + " WORLD",
            error -> "FALLBACK"
        );
    $result; // Expected: "HELLO WORLD"
}

{
    argument = list(
        {"name": "Alice", "age": "25"},
        {"name": "Bob", "age": "invalid"},
        {"name": "Charlie", "age": "30"}
    );

    result = $argument.try(
        people -> $people.map(person -> {
            validAge = $person["age"].try(
                ageStr -> numberFormat("#").parse($ageStr),
                error -> 0
            );
            {
                "name": $person["name"],
                "age": $validAge,
                "valid": $validAge > 0
            };
        }),
        error -> list()
    );
    $result;
}

{
    argument = {
        "users": list("user1", "user2", "user3"),
        "operation": "validate"
    };

    result = $argument.try(
        config -> {
            users = $config["users"];
            operation = $config["operation"];

            processedUsers = $users.try(
                userList -> $userList
                    .filter(user -> $user != null)
                    .map(user -> {
                        processed = $user.try(
                            u -> toUpperCase($u),
                            error -> "INVALID_USER"
                        );
                        $processed;
                    }),
                error -> list("FALLBACK_USER")
            );

            {
                "operation": $operation,
                "processedUsers": $processedUsers,
                "count": $processedUsers.size(),
                "status": "success"
            };
        },
        error -> {
            "operation": "unknown",
            "processedUsers": list(),
            "count": 0,
            "status": "failed",
            "error": toString($error)
        }
    );
    $result;
}

{
    argument = "";
    result = $argument.try(
        emptyStr -> {
            if($emptyStr.length() == 0, 
               throw("Empty string not allowed"), 
               $emptyStr);
        },
        error -> "Default value for empty input"
    );
    $result;
}

{
    argument = "";
    result = $argument.try(
        emptyStr -> {
            if($emptyStr.length() == 0, 
               throw("Empty string not allowed"), 
               $emptyStr);
        }
    );
    $result == null;
}
