value.value:
value -> self -> {
  container = $self.referers(`test.containment.constraint:Container#content`).singleElement();
  contents = $container.get(`test.containment.constraint:Container#content`);
  index = $contents.elementIndex($self);
  predecessor = $contents.elementAt($index - 1);
  predecessorValue = $predecessor.get(`test.containment.constraint:Contents#value`);
  if ($value < $predecessorValue, #('Der Wert muss mindestens {0} sein.'@de).fill($predecessorValue));
}
