value.value:
{
  twoDigits = numberFormat("00");
  fourDigits = numberFormat("0000");
  cal = all(`DemoTypes:BNotUnderA`)
      .filter(a -> $a.get(`DemoTypes:BNotUnderA#name`) == "TestTimeAttributes").singleElement()
      .get(`tl.element:StructuredElementContainer#children`)
      .filter(a -> $a.get(`DemoTypes:A#name`) == "A0").singleElement()
      .get(`DemoTypes:A#time`).toUserCalendar();
  $twoDigits.format($cal.day()) + "-" + $twoDigits.format($cal.month() + 1) + "-" + $fourDigits.format($cal.year()) + " " + $twoDigits.format($cal.hour()) + ":" + $twoDigits.format($cal.minute());
}

